// src/controllers/ScheduleController.js
import Schedule from "../models/Schedule";

class ScheduleController {
  async getAll() {
    // ðŸ”´ Táº M THá»œI DATA Cá»¨NG (Ä‘á»ƒ cháº¯c cháº¯n UI hiá»‡n)
    return [
      new Schedule({
        id: 1,
        movie: "Avengers",
        date: "2025-11-15",
        time: "19:30",
        room: "P1"
      }),
      new Schedule({
        id: 2,
        movie: "Titanic",
        date: "2025-12-01",
        time: "20:00",
        room: "P2"
      })
    ];
  }

  async create(data) {
    return new Schedule({ id: Date.now(), ...data });
  }

  async delete(id) {
    return true;
  }
}

export default ScheduleController();
